// Data Export
core => {
  core.scriptManager.addEvents({
    doctype: "Data Export",
    eventDict: {
      form_setup: (c, doc) => {
        addExportButton(doc);
      },
      reference_doctype: (c, doc) => {
        const doctype = doc.reference_doctype;
        if (doctype) {
          showSpinner(true);
          core.meta.getDocMeta({ doctype }).then(r => {
            showSpinner(false);
            if (r.success) {
              setFieldOptions(doc, doctype, r.data);
              setTimeout(() => {
                window.RSdataExportSelectAll(doctype, true);
              }, 500);
            } else {
              showMessage("red", "Failed loading doctype, Please try again");
            }
          });
        } else {
          resetFilterAndFields(doc);
        }
      }
    }
  });

  window.setDataExportFieldEnableDisabled = (doctype, fieldname, e) => {
    const d = getFieldSelection();
    d[doctype] = d[doctype] || [];
    const arr = d[doctype];
    const idx = arr.indexOf(fieldname);
    if (e && idx < 0) {
      d[doctype].push(fieldname);
    } else if (!e && idx >= 0) {
      arr.splice(idx, 1);
    }
  };

  window.RSdataExportSelectAll = (doctype, selectAll) => {
    for (const e of document.getElementsByClassName(
      `checkbox-dataexport-${doctype}`
    )) {
      e.checked = selectAll;
      e.onchange();
    }
  };

  let dataexport_fieldselection = {};
  function setFieldSelection(v) {
    dataexport_fieldselection = v || {};
  }

  function getFieldSelection() {
    return dataexport_fieldselection || {};
  }

  function exportDoc(doc) {
    showSpinner(true);
    fetch(
      core.config.hostUrl +
        "/api/method/frappe.core.doctype.data_export.exporter.export_data",
      {
        mode: "cors",
        method: "POST",
        headers: {
          Authorization: "Token " + core.auth.getCurrentToken(),
          "X-Requested-With": "XMLHttpRequest",
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          // stringify selection columns for frapep access logs
          select_columns: JSON.stringify(getFieldSelection()),
          doctype: doc.reference_doctype,
          filters: [], // TODO
          file_type: doc.file_type,
          template: true,
          with_data: 1
        })
      }
    )
      .then(async r => {
        showSpinner(false);
        if ((r.status / 100) === 2) return r.blob();
        return Promise.reject(await r.text());
      })
      .then(b => {
        let objUrl = window.URL.createObjectURL(b);
        var a = document.createElement("a");
        document.body.appendChild(a);
        a.style = "display: none";
        a.href = objUrl;
        a.download = `${doc.reference_doctype}.${
          doc.file_type === "Excel" ? "xlsx" : "csv"
        }`;
        a.click();
        window.URL.revokeObjectURL(objUrl);
      })
      .catch((e) => {
        console.error("Export Error", e);
        showMessage("red", "Something went wrong while exporting, Please try again");
      });
  }

  function setFieldOptions(doc, doctype, meta) {
    setFieldSelection({});
    setDocValue(
      doc,
      "filter_list",
      "<div class='text-muted'>Filters! (Coming Soon)</div>"
    );

    const p = [getDocTypeFieldsHTML(doctype, doctype)];
    for (const t of meta.fields.filter(f => f.fieldtype === "Table")) {
      p.push(getDocTypeFieldsHTML(t.options, doctype));
    }

    Promise.all(p)
      .then(htmls => {
        return htmls.reduce((a, b) => a + b, "");
      })
      .then(html => {
        html = `
        <button onclick="RSdataExportSelectAll('${doctype}', true)">Select All</button>
        <button onclick="RSdataExportSelectAll('${doctype}', false)">Unselect All</button>
        ${html}
        `;
        setDocValue(doc, "fields_multicheck", html);
      });
  }

  function getDocTypeFieldsHTML(doctype, ogDocType) {
    return core.meta
      .getDocMeta({ doctype })
      .then(r => {
        if (!r.success) {
          return Promise.reject("");
        }
        const meta = r.data;
        const colsHtml = ["", "", ""];
        let currentCol = 0;
        for (const f of getFields(meta)) {
          colsHtml[currentCol++] += `
          <label>
            <input class="checkbox-dataexport-${ogDocType}" type="checkbox" data-doctype="${doctype}" data-fieldname="${f.fieldname}"
              onchange="setDataExportFieldEnableDisabled('${doctype}', '${f.fieldname}', this.checked)"/> 
            ${f.label}
          </label>
        `;
          currentCol = currentCol % 3;
        }

        return `
      <div class="mt-3 mb-2 uppercase text-muted label">${doctype}</div>
      <div class="row">
        <div class="col-4 d-flex flex-column">${colsHtml[0]}</div>
        <div class="col-4 d-flex flex-column">${colsHtml[1]}</div>
        <div class="col-4 d-flex flex-column">${colsHtml[2]}</div>
      </div>
      `;
      })
      .catch(r => {
        return "Failed template generation";
      });
  }

  function addExportButton(doc) {
    core.bus.post({
      id: "form_add_custom_button",
      data: {
        label: "Export Data",
        onclick: (c, d) => {
          if (!doc.reference_doctype) {
            showMessage("red", "Please select a doctype");
            return;
          }
          exportDoc(doc);
        }
      }
    });
  }

  function resetFilterAndFields(doc) {
    setDocValue(doc, "filter_list", "");
    setDocValue(doc, "fields_multicheck", "");
  }

  function setDocValue(doc, fieldname, value) {
    runInZone(() => {
      doc[fieldname] = value;
      if (doc.doctype) {
        refreshField(doc.doctype, fieldname);
      }
    });
  }

  function runInZone(fn) {
    if (window["Zone"] && window.Zone.current) {
      Zone.current.run(fn);
    } else {
      fn();
    }
  }

  function getFields(docmeta) {
    const noValueTypes = [
      "Section Break",
      "Column Break",
      "HTML",
      "Table",
      "Table MultiSelect",
      "Button",
      "Image",
      "Fold",
      "Heading"
    ];
    return docmeta.fields.filter(f => {
      return !f.hidden && noValueTypes.indexOf(f.fieldtype) === -1;
    });
  }

  function refreshField(doctype, fieldname) {
    core.ui.refreshField(doctype, fieldname);
  }

  function showSpinner(show) {
    core.bus.post({
      id: "spinner_service",
      data: {
        show: show
      }
    });
  }

  function showMessage(indicator, msg) {
    core.messages.next({
      indicator,
      message: msg
    });
  }
};

// Data Import
// tslint:disable-next-line: no-unused-expression
core => {

  function showSpinner(show) {
    core.bus.post({
      id: "spinner_service",
      data: {
        show: show
      }
    });
  }

  function setupImportLog(doc) {
    doc.import_log = "";
    if (!doc.log_details) {
      return;
    }
    const logs = (JSON.parse(doc.log_details) || {}).messages;
    if (!logs || logs.length === 0) {
      return;
    }
    console.log("Import Logs", logs);
    let rows = "";
    for (const l of logs) {
      if (doc.show_only_errors && l.indicator !== "red") {
        continue;
      }
      rows += `
        <tr>
          <td>${l.row}</td>
          <td>
            <span class="indicator ${l.indicator}">
              ${l.title.replace(/(desk)?#Form/, "\/#/form")}
            </span>
          </td>
          <td>
            ${l.message.replace(/(desk)?#Form/, "\/#/form")}
            <a href="${l.link.replace(/(desk)?#Form/, "\/#/form")}">
              <span class="eva eva-arrow-circle-right float-right data-import-log-link"></span>
            </a>
          </td>
        </tr>
      `;
      console.log(l);
    }
    doc.import_log = `
    <style>
      .indicator::before {
        margin: 0px 4px 0px 0px;
        content: '';
        display: inline-block;
        height: 8px;
        width: 8px;
        border-radius: 8px;
      }
      .indicator.red::before {
        background: red;
      }
      .indicator.green::before {
        background: green;
      }
      .eva.data-import-log-link {
        font-size: 1.5em;
      }
    </style>
    <table class="table table-bordered table-striped">
      <thead>
        <tr>
          <th>Row No.</th>
          <th>Row Status</th>
          <th>Message</th>
        </tr>
      </thead>
      <tbody>
        ${rows}
      </tbody>
    </table>
    `;
    rcore.ui.refreshField("Data Import", "import_log")
  }

  function addStartImportButton(doc) {
    if (doc.reference_doctype && doc.import_file && doc.total_rows &&
      doc.docstatus === 0 && (!doc.import_status || doc.import_status === "Failed")) {
        core.bus.post({
          id: "form_add_custom_button",
          data: {
            label: "Start Import",
            onclick: (c, d) => {
              showSpinner(true);
              core.call({
                cmd: "frappe.core.doctype.data_import.data_import.import_data",
                data_import: doc.name
              }).then((r) => {
                showSpinner(false);
                if (r.success) {
                  core.messages.next({
                    message: "Please reload this document after 5 seconds to see the status",
                    indicator: "green"
                  });
                } else {
                  core.messages.next({
                    message: "Failed starting the import, please try again",
                    indicator: "red"
                  });
                }
              });
            }
          }
        });
      }
  }

  core.scriptManager.addEvents({
    doctype: "Data Import",
    eventDict: {
      form_setup: (c, doc) => {
        addStartImportButton(doc);
        setupImportLog(doc);
      },
      show_only_errors: (c, doc) => {
        setupImportLog(doc);
      },
      action: (core, doc) => {
        if(doc.action == "Insert new records") {
          doc.insert_new = 1;
          doc.overwrite = 0;
        } else if (doc.action == "Update records"){
          doc.insert_new = 0;
          doc.overwrite = 1;
        }
      }
    }
  });
};

// Broadcast Message
core => {
  const showSpinner = () =>
    core.bus.post({ id: "spinner_service", data: { show: true } });
  const hideSpinner = () =>
    core.bus.post({ id: "spinner_service", data: { show: false } });

  core.scriptManager.addEvents({
    doctype: "Broadcast Message",
    eventDict: {
      form_setup: (core, doc) => {
        addSendButton(core, doc);
      }
    }
  });

  function addSendButton(core, doc) {
    if (doc.docstatus !== 1) {
      return;
    }
    core.bus.post("form_add_custom_button", {
      label: "Send Message",
      onclick: async (core, doc) => {
        showSpinner();
        const r = await core.call({
          cmd: "runserverobj",
          method: "send",
          dt: doc.doctype,
          dn: doc.name
        });
        if (!r.success) {
          core.messages.next({
            message: "Something went wrong, Please try again!",
            indicator: "red"
          });
        }
        hideSpinner();
      }
    });
  }
};
